import ApiClient from './ApiClient';


export const __LoginUser = async (userData) => {
    try {
        const res = await ApiClient.post('/user/login', userData);
        loocalStorage.setItem('token', res.data.token);
        return res.data;
    } catch (error) {
        throw error
    }
}

export const __RegisterUser = async (formData) => {
    try {
        const res = await ApiClient.post('/user/register', formData);
        return res.data;
    } catch (error) {
        throw error
    }
}

export const __CheckSession = async () => {
    try {
        const res = await ApiClient.get('/users/refresh/session');
        console.log(res.data);
        return res.data
    } catch (error) {
        throw error;
    }
}
